package lifecycle02;

public class cuppuccino {
	private int price;

	public int getPrice() {
		return price;
	}

	public void setPrice(int price) {
		this.price = price;
	}

	public cuppuccino() {
		super();
		// TODO Auto-generated constructor stub
	}

	@Override
	public String toString() {
		return "cuppuccino [price=" + price + "]";
	}

	
	

}
